INSERT INTO bw_zfbz VALUES('4','南康前锦家教网资费标准','','','<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"700\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"border-bottom: #ebecee 1px solid; border-left: #ebecee 1px solid; padding-bottom: 20px; line-height: 20px; padding-left: 20px; padding-right: 20px; border-top: #ebecee 1px solid; border-right: #ebecee 1px solid; padding-top: 20px\">\r\n				<span class=\"red\"><strong>特别提示：</strong></span>本价格仅供家长、教员参考，具体价格需教员与学员（家长）协商。</td>\r\n		</tr>\r\n		<tr>\r\n			<td height=\"50\">\r\n				<div align=\"center\">\r\n					<strong class=\"buleo2_link_9\">中小学文化课类教员课时费</strong>（元/每小时）</div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-bottom: #ebecee 1px solid; border-left: #ebecee 1px solid; padding-bottom: 20px; line-height: 20px; padding-left: 20px; padding-right: 20px; border-top: #ebecee 1px solid; border-right: #ebecee 1px solid; padding-top: 20px\">\r\n				<table align=\"center\" bgcolor=\"#ffffff\" border=\"0\" cellspacing=\"1\" width=\"660\">\r\n					<tbody>\r\n						<tr bgcolor=\"#77aaff\">\r\n							<td bgcolor=\"#ffcc33\" height=\"30\">\r\n								<div align=\"center\">\r\n									语文/数学/物理/化学/生物</div>\r\n							</td>\r\n							<td bgcolor=\"#0588d6\" class=\"white\" width=\"105\">\r\n								<div align=\"center\">\r\n									幼儿、小学</div>\r\n							</td>\r\n							<td bgcolor=\"#0588d6\" class=\"white\" width=\"105\">\r\n								<div align=\"center\">\r\n									初中</div>\r\n							</td>\r\n							<td bgcolor=\"#0588d6\" class=\"white\" width=\"105\">\r\n								<div align=\"center\">\r\n									高中</div>\r\n							</td>\r\n						</tr>\r\n						<tr bgcolor=\"#ecf3ff\">\r\n							<td height=\"30\">\r\n								<div align=\"center\">\r\n									大学生</div>\r\n							</td>\r\n							<td>\r\n								<div align=\"center\">\r\n									25-30</div>\r\n							</td>\r\n							<td>\r\n								<div align=\"center\">\r\n									30-40</div>\r\n							</td>\r\n							<td>\r\n								<div align=