DROP TABLE IF EXISTS bw_ad;
CREATE TABLE bw_ad (
  id int(10) unsigned NOT NULL auto_increment COMMENT '索引ID',
  title varchar(100) default NULL COMMENT '广告描述',
  url varchar(200) default NULL COMMENT '广告链接',
  pic varchar(200) default NULL COMMENT '图片存储路径',
  isshow int(1) default NULL COMMENT '1显示,2隐藏',
  picsm varchar(100) default NULL COMMENT '图片说明',
  PRIMARY KEY  (id),
  UNIQUE KEY pic (pic)
) ENGINE=MyISAM AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS bw_article;
CREATE TABLE bw_article (
  id int(10) unsigned NOT NULL auto_increment COMMENT '索引ID',
  title varchar(200) NOT NULL COMMENT '文章标题',
  author varchar(20) NOT NULL COMMENT '文章作者',
  levels tinyint(1) NOT NULL COMMENT '层级',
  froms varchar(200) NOT NULL COMMENT '文章来源',
  classId int(10) NOT NULL COMMENT '类别ID',
  content text NOT NULL COMMENT '文章内容',
  subTime datetime NOT NULL COMMENT '发布时间',
  hits int(10) NOT NULL COMMENT '点击量',
  pic varchar(200) default NULL COMMENT '图片',
  lang varchar(2) default NULL COMMENT '语言版本标记',
  wjgs varchar(45) default NULL COMMENT '文件格式',
  PRIMARY KEY  (id)
) ENGINE=MyISAM AUTO_INCREMENT=104 DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS bw_articleclass;
CREATE TABLE bw_articleclass (
  id int(10) unsigned NOT NULL auto_increment COMMENT '索引ID',
  className varchar(100) NOT NULL COMMENT '类别名称',
  path varchar(50) NOT NULL COMMENT '层级路径',
  parentId int(10) NOT NULL COMMENT '父ID',
  lang varchar(2) NOT NULL default 'cn' COMMENT '语言版本标记',
  PRIMARY KEY  (id)
) ENGINE=MyISAM AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS bw_base;
CREATE TABLE bw_base (
  id int(10) unsigned NOT NULL auto_increment COMMENT '索引ID',
  title varchar(200) default NULL COMMENT '栏目名称',
  classId int(4) default NULL COMMENT '栏目类别',
  description varchar(200) default NULL COMMENT '基本描述',
  content text COMMENT '详细信息',
  lang varchar(45) default NULL COMMENT '语言版本标记',
  PRIMARY KEY  (id)
) ENGINE=MyISAM AUTO_INCREMENT=29 DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS bw_config;
CREATE TABLE bw_config (
  id int(2) unsigned NOT NULL auto_increment COMMENT '索引ID',
  nianji text COMMENT '年级设置',
  title varchar(200) default '网站标题' COMMENT '网站标题',
  keyword varchar(500) default '关键字' COMMENT '关键字',
  description varchar(500) default '网站描述' COMMENT '网站描述',
  information text NOT NULL COMMENT '底部说明信息',
  logo varchar(200) default 'logo存储地址' COMMENT 'logo存储地址',
  lang varchar(45) NOT NULL COMMENT '语言版本标记',
  xueli text COMMENT '学历设置',
  shenfen text COMMENT '目前身份',
  zhicheng text COMMENT '职称',
  quyu text NOT NULL COMMENT '区域',
  leibie text NOT NULL COMMENT '任教学校类别',
  xyxxly text COMMENT 'xyxxly',
  qjjphone varchar(100) default NULL COMMENT 'è¯·å®¶æ•™çƒ­çº¿ç”µè¯',
  jyphone varchar(100) default NULL COMMENT 'æ•™å‘˜çƒ­çº¿ç”µè¯',
  bphone varchar(100) default NULL COMMENT 'bennerçƒ­çº¿ç”µè¯',
  PRIMARY KEY  (id),
  UNIQUE KEY lang (lang)
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS bw_dxmb;
CREATE TABLE bw_dxmb (
  id int(10) NOT NULL auto_increment COMMENT 'id',
  title varchar(200) default NULL COMMENT '标题',
  content text COMMENT '内容',
  addtime datetime default NULL COMMENT '时间',
  lang varchar(45) default NULL COMMENT '分站版本',
  PRIMARY KEY  (id)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS bw_friendlink;
CREATE TABLE bw_friendlink (
  id int(10) unsigned NOT NULL auto_increment COMMENT '索引ID',
  `type` tinyint(1) NOT NULL default '1' COMMENT '1文字, 2图片',
  classId tinyint(4) NOT NULL default '1' COMMENT '是友情链接,品牌企业等等',
  title varchar(200) NOT NULL COMMENT '网站名称',
  url varchar(200) NOT NULL COMMENT 'url',
  pic varchar(200) NOT NULL COMMENT '图片存储位置',
  information text NOT NULL COMMENT '链接基本描述',
  isshow tinyint(1) NOT NULL COMMENT '1启用 2不启用',
  PRIMARY KEY  (id)
) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS bw_lang;
CREATE TABLE bw_lang (
  id int(10) NOT NULL auto_increment,
  lang text COMMENT '分站名称',
  PRIMARY KEY  (id)
) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS bw_member;
CREATE TABLE bw_member (
  id int(10) unsigned NOT NULL auto_increment COMMENT '索引ID',
  username varchar(100) default NULL COMMENT '用户名',
  `password` varchar(200) default NULL COMMENT '密码',
  reg_time datetime default NULL COMMENT '注册时间',
  sex int(1) default '1' COMMENT '性别',
  idtype int(1) default '1' COMMENT '证件类型',
  idcode varchar(200) default NULL COMMENT '证件号码',
  truename varchar(20) default NULL COMMENT '真实姓名',
  companytype tinyint(1) default '1' COMMENT '1饮水设备2净水设备3工程设备',
  company varchar(200) default '尚未提交公司名称' COMMENT '公司名称',
  birthday varbinary(20) default NULL COMMENT '出生日期',
  address varchar(200) default NULL COMMENT '联系地址',
  telphone varchar(100) default NULL COMMENT '联系电话',
  jifen int(10) unsigned default NULL COMMENT '积分',
  levels int(1) default '1' COMMENT '层级(扩展)',
  mbid int(10) default NULL COMMENT '模板id',
  isshow tinyint(1) default '2' COMMENT '1显示2不显示',
  dltime datetime default NULL COMMENT '最后登录时间',
  Money float default '0',
  jiaolin varchar(10) default NULL COMMENT '教龄',
  ifrz int(1) default '1' COMMENT '是否认证',
  ifxj int(1) default '6' COMMENT '是否星级',
  csd varchar(200) default '没有填写' COMMENT '出生地',
  xuexiao varchar(200) default '没有填写' COMMENT '毕业学校',
  xueli varchar(100) default '没有填写' COMMENT '最后学历',
  zhuanye varchar(100) default '没有填写' COMMENT '专业',
  mqsf varchar(100) default '没有填写' COMMENT '目前身份',
  quyu varchar(200) default '没有填写' COMMENT '区域',
  zhicheng varchar(200) default '没有填写' COMMENT '职称等级',
  rzxxlb varchar(200) default '没有填写' COMMENT '任职学校类别',
  rjxk varchar(500) default '没有填写' COMMENT '任教学科',
  kjskm longtext COMMENT '可教授科目',
  zwms longtext COMMENT '自己描述',
  kskqy varchar(200) default NULL COMMENT '可授课区域',
  kfdfs varchar(500) default '没有填写' COMMENT '可辅导方式',
  ksksj varchar(500) default NULL COMMENT '可授课时间',
  xsyq varchar(500) default NULL COMMENT '薪水要求',
  email varchar(100) default NULL COMMENT '邮箱',
  City int(100) default '0' COMMENT '城市',
  qq varchar(20) default NULL COMMENT 'qq',
  sklx varchar(20) default NULL COMMENT '授课类型',
  xxly varchar(100) default NULL COMMENT '信息来源',
  zhrzxx varchar(200) default NULL COMMENT '最后任职学校',
  guojia varchar(50) default NULL COMMENT '留学国家或是国籍',
  sfzpic varchar(500) default NULL COMMENT '身份证图片',
  byzpic varchar(500) default NULL COMMENT '毕业证图片',
  zgzspic varchar(500) default NULL COMMENT '资格证书图片',
  iffb int(1) default '1' COMMENT '是否发布',
  ifzc int(1) default '1' COMMENT '是否是专才',
  daoqi datetime default NULL COMMENT '到时时间',
  iftj int(1) default '1' COMMENT '是否推荐',
  tupic varchar(500) default NULL COMMENT '相片',
  gzjl text COMMENT '跟踪记录(备注)',
  dxjs int(10) default '1' COMMENT '短信接收，1接收，2不接受',
  byid int(10) default NULL COMMENT '备用id',
  hits int(10) default '0' COMMENT '浏览次数',
  shqk int(10) default '-1' COMMENT '审核情况，-1待审核，0不通过，1通过',
  zjtime datetime default NULL COMMENT '最近登录时间',
  dlcs int(10) default '0' COMMENT '登录次数',
  lang varchar(45) default '厦门站' COMMENT '分站版本',
  ifnew int(1) default NULL COMMENT '是否最新提示，1提示，0不提示',
  jiance varchar(300) default NULL,
  jiance2 varchar(300) default NULL,
  uname varchar(200) default NULL COMMENT '审核人',
  llq varchar(200) default NULL COMMENT '浏览器',
  PRIMARY KEY  (id),
  UNIQUE KEY bw_member_id (id)
) ENGINE=MyISAM AUTO_INCREMENT=104 DEFAULT CHARSET=utf8;

