DROP TABLE IF EXISTS bw_order;
CREATE TABLE bw_order (
  id int(10) NOT NULL auto_increment COMMENT '订单id',
  xyid int(10) default NULL COMMENT '学员id',
  jyid int(10) default NULL COMMENT '教员id',
  ddqk text COMMENT '订单情况',
  ddzt int(1) default '1' COMMENT '订单状态,1试教中2，订单成功，3，订单失败',
  yylx int(1) default '1' COMMENT '1表示教员预约学员2表示学员预约教员',
  username varchar(200) default NULL COMMENT '后台操作人',
  subtime datetime default NULL COMMENT '预约提交时间',
  addtime datetime default NULL COMMENT '订单时间',
  ifdd int(1) default '1' COMMENT '是否订单：1，代表不是，2，代表是',
  yyqk varchar(200) default NULL COMMENT '预约情况',
  lang varchar(45) NOT NULL COMMENT '分站版本',
  cltime datetime default NULL COMMENT '处理订单时间',
  ifnew int(1) default NULL COMMENT '是否最新提示，1提示，0不提示',
  PRIMARY KEY  (id)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

