DROP TABLE IF EXISTS bw_user;
CREATE TABLE bw_user (
  id int(10) unsigned NOT NULL auto_increment COMMENT '索引ID',
  username varchar(100) NOT NULL COMMENT '用户名',
  `password` varchar(100) NOT NULL COMMENT '密码',
  reg_time datetime default NULL COMMENT '注册时间',
  lastLoginTime datetime default NULL COMMENT '最后登录时间',
  quanxian text COMMENT '权限标记',
  bumen varchar(300) default NULL COMMENT '部门',
  parentid int(10) default NULL COMMENT '上级ID',
  lang varchar(100) default NULL COMMENT '站点区域',
  PRIMARY KEY  (id),
  UNIQUE KEY username (username)
) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS bw_userproclass;
CREATE TABLE bw_userproclass (
  id int(10) unsigned NOT NULL auto_increment COMMENT '索引ID',
  className varchar(100) NOT NULL COMMENT '类名',
  path varchar(50) default NULL COMMENT '保存路径',
  parentId int(10) unsigned NOT NULL default '0' COMMENT '父ID',
  paixu int(12) default NULL COMMENT '大类排序',
  PRIMARY KEY  (id)
) ENGINE=MyISAM AUTO_INCREMENT=95 DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS bw_withdraw;
CREATE TABLE bw_withdraw (
  id int(10) unsigned NOT NULL auto_increment COMMENT '索引ID',
  mid int(10) default NULL COMMENT '会员ID',
  amount int(10) default NULL COMMENT '退款金额',
  reason varchar(500) default NULL COMMENT '退款理由',
  ispass tinyint(1) default NULL COMMENT '1通过,2未通过',
  addtime datetime default NULL COMMENT '操作时间',
  memo longtext COMMENT '情况备注',
  yinh varchar(200) default NULL COMMENT '退款银行',
  huming varchar(200) default NULL COMMENT '退款户名',
  zhanghao varchar(200) default NULL COMMENT '银行账号',
  add1 varchar(200) default NULL COMMENT '开卡所在地',
  lang varchar(45) default NULL COMMENT '分站版本',
  PRIMARY KEY  (id)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS bw_yuyue;
CREATE TABLE bw_yuyue (
  id int(10) NOT NULL auto_increment COMMENT '预约id',
  yyrid int(10) default NULL COMMENT '预约人id',
  byyrid int(10) default NULL COMMENT '被预约人id',
  yystyle int(1) default '1' COMMENT '1代表教员预约学员，2代表学员预约教员',
  addtime datetime default NULL COMMENT '预约时间',
  PRIMARY KEY  (id)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS bw_zfbz;
CREATE TABLE bw_zfbz (
  id int(10) unsigned NOT NULL auto_increment COMMENT '索引ID',
  title varchar(200) default NULL COMMENT '栏目名称',
  classId int(4) default NULL COMMENT '栏目类别',
  description varchar(200) default NULL COMMENT '基本描述',
  content text COMMENT '详细信息',
  lang varchar(45) default NULL COMMENT '语言版本标记',
  PRIMARY KEY  (id)
) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS bw_zxwd;
CREATE TABLE bw_zxwd (
  id int(10) NOT NULL auto_increment,
  uid int(10) default NULL COMMENT '用户ID',
  addtime timestamp NULL default CURRENT_TIMESTAMP COMMENT '提交时间',
  ifdl int(3) default '1' COMMENT '是否删除，1不删除，2删除',
  xxlx int(10) default '1' COMMENT '信息类型，1单条信息，2群发信息',
  username varchar(50) default NULL COMMENT '会员名称',
  content text COMMENT '内容',
  title varchar(200) default NULL COMMENT '标题',
  lang varchar(45) default NULL COMMENT '分站版本',
  PRIMARY KEY  (id)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS bw_zxwdhf;
CREATE TABLE bw_zxwdhf (
  id int(10) NOT NULL auto_increment,
  addtime datetime default NULL COMMENT '提交时间',
  ifdl int(3) default '1' COMMENT '是否删除，1不删除，2删除',
  username varchar(50) default NULL COMMENT '会员名',
  uid int(10) default NULL COMMENT '用户ID',
  hfid int(10) default NULL COMMENT '回答问题的ID',
  content text COMMENT '内容',
  title varchar(200) default NULL COMMENT '标题',
  xxlx int(10) default '1' COMMENT '信息类型，1单条信息，2群发信息',
  lang varchar(45) default NULL COMMENT '分站版本',
  ifyd int(1) default '1' COMMENT '是否阅读,1为阅读,0已阅读',
  PRIMARY KEY  (id)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;




















